import React, {useState} from 'react';
import {
    Box,
    Button,
    Flex,
    FormControl,
    FormLabel,
    Heading,
    HStack,
    Input, Tag, TagCloseButton, TagLabel, Text,
    Textarea,
    useToast,
    VStack, Wrap, WrapItem
} from "@chakra-ui/react";
import DatePicker from "react-datepicker";

const Assignment = () => {

    const [emails, setEmails] = useState([]);
    const [email, setEmail] = useState("");
    const [finishDate,setFinishDate] = useState(new Date());
    const toast = useToast();

    return (
        <Box w={'100%'} minH={'100vh'}>
            <Flex w={'100%'}>
                <VStack flex={8} w={'100%'} spacing={2}>
                    {/*TITLE*/}
                    <HStack w={'100%'}>
                        <FormControl id="title" isRequired>
                            <FormLabel>Title</FormLabel>
                            <Input bg={'white'} placeholder="Title"/>
                        </FormControl>
                    </HStack>
                    {/*    DESCRIPTION*/}
                    <FormControl id="Description" isRequired>
                        <FormLabel>Description</FormLabel>
                        <Textarea bg={'white'} placeholder="Description"/>
                    </FormControl>
                </VStack>
                <Box flex={3} bg={'white'} p={2} mx={2}>
                    <VStack align={'start'} w={'100%'}>
                        <Button alignSelf={'end'} colorScheme={'blue'}>Giao BÃ i</Button>
                        <VStack w={'100%'} align={'start'}>
                            <Text>Deadline</Text>
                            <DatePicker
                                selected={finishDate}
                                onChange={(date) => setFinishDate(date)}
                                timeInputLabel="Time:"
                                dateFormat="MM/dd/yyyy h:mm aa"
                                showTimeInput
                            />
                        </VStack>
                        <VStack>
                            <Wrap w={"100%"}>
                                {emails.map((email) => (
                                    <WrapItem key={email}>
                                        <Tag
                                            size={"sm"}
                                            borderRadius="full"
                                            variant="solid"
                                            colorScheme="green"
                                        >
                                            <TagLabel>{email}</TagLabel>
                                            <TagCloseButton
                                                onClick={() => {
                                                    let filteredEmail =
                                                        emails.filter(
                                                            (e) => e != email
                                                        );
                                                    setEmails(filteredEmail);
                                                }}
                                            />
                                        </Tag>
                                    </WrapItem>
                                ))}
                            </Wrap>
                            {/*INPUT & ADD BUTTOn*/}
                            <HStack w={"100%"}>
                                <Input
                                    size={"sm"}
                                    value={email}
                                    onChange={(e) => setEmail(e.target.value)}
                                    placeholder={
                                        "Email to share. Ex: test@gmail.com"
                                    }
                                />
                                <Button
                                    onClick={() => {
                                        setEmail("");
                                        if (!emails.includes(email)) {
                                            setEmails((prevState) => [
                                                ...prevState,
                                                email,
                                            ]);
                                        }
                                    }}
                                    isDisabled={email.length === 0}
                                    size={"sm"}
                                    colorScheme={"purple"}
                                >
                                    Add
                                </Button>
                            </HStack>
                        </VStack>
                    </VStack>
                </Box>
            </Flex>
        </Box>
    );
};

export default Assignment;
