import React, {useContext, useEffect, useState} from 'react';
import {
    Box,
    Button,
    Flex,
    FormControl,
    FormLabel, forwardRef,
    Heading,
    HStack,
    Input, Tag, TagCloseButton, TagLabel, Text,
    Textarea,
    useToast,
    VStack, Wrap, WrapItem
} from "@chakra-ui/react";
import DatePicker from "react-datepicker";
import {useParams} from "react-router-dom";
import {QuizEditContext} from "../../../providers/QuizEditProvider";
import {AdminContext} from "../../../providers/AdminSettingProvider";
import UserService from "../../../service/UserService";
import dateFormat from "dateformat";

const Assignment = () => {

    const {getQuizDetail, quiz} = useContext(QuizEditContext);
    const {isLoading, setLoading} = useContext(AdminContext);
    const {id} = useParams();
    const [email, setEmail] = useState("");
    const [err, setErr] = useState('');
    const [assignment, setAssignment] = useState({
        quizId: id,
        emails: [],
        startDate: new Date(),
        // finishDate: new Date()
    })
    const toast = useToast();
    const ExampleCustomInput = forwardRef(({value, onClick}, ref) => (
        <button className="example-custom-input" onClick={onClick} ref={ref}>
            {value}
        </button>
    ));
    useEffect(() => {
        console.log(id);
        getQuizDetail(id);
    }, [])
    const assignToUsers = async () => {
        // setAssignment(prev => ({
        //     ...prev,
        //     startDate: formatDate(prev.startDate),
        //     finishDate: formatDate(prev.finishDate)
        // }))
        if (assignment.startDate != null && assignment.finishDate != null && assignment.title != null) {
            console.log('assigning....')
            try {
                const data = await UserService.assignToUsers(assignment);
                console.log("assign success")
            } catch (e) {
                console.log("Could not assign quiz to users! ", e)
            }
        }
        console.log(assignment)
    }
    const formatDate = (date) => {
        return dateFormat(date, "yyyy-MM-dd HH:mm").toString();
    }
    return (
        <Box w={'100%'} minH={'100vh'}>
            {
                !isLoading && (
                    <VStack w={'100%'}>
                        <Box p={5} bg={'white'} w={'100%'}>
                            <HStack>
                                <Text>{quiz && quiz.title}</Text>
                            </HStack>
                        </Box>
                        <Flex w={'100%'}>
                            <VStack flex={8} w={'100%'} spacing={2}>
                                {/*TITLE*/}
                                <HStack w={'100%'}>
                                    <FormControl id="title" isRequired>
                                        <FormLabel>Title</FormLabel>
                                        <Input
                                            onChange={(e) =>
                                                setAssignment(prev => (
                                                    {
                                                        ...prev,
                                                        title: e.target.value
                                                    }
                                                ))}
                                            value={assignment.title}
                                            bg={'white'} placeholder="Title"/>
                                    </FormControl>
                                </HStack>
                                {/*    DESCRIPTION*/}
                                <FormControl id="Description" isRequired>
                                    <FormLabel>Description</FormLabel>
                                    <Textarea
                                        onChange={(e) =>
                                            setAssignment(prev => (
                                                {
                                                    ...prev,
                                                    description: e.target.value
                                                }
                                            ))}
                                        value={assignment.description}

                                        bg={'white'} placeholder="Description"/>
                                </FormControl>
                            </VStack>
                            <Box flex={3} bg={'white'} p={2} mx={2} minH={'100vh'}>
                                <VStack align={'start'} w={'100%'} spacing={5}>

                                    <VStack w={'100%'} align={'start'}>
                                        <Text>Deadline</Text>

                                        <DatePicker
                                            customInput={<ExampleCustomInput/>}
                                            selected={assignment.startDate}

                                            onChange={(date) => setAssignment((prev) => ({
                                                ...prev,
                                                startDate: date
                                            }))}
                                            showTimeSelect
                                            timeFormat="HH:mm"

                                            dateFormat="yyyy-MM-dd HH:mm"
                                        />
                                        <DatePicker
                                            customInput={<ExampleCustomInput/>}
                                            selected={assignment.finishDate}

                                            onChange={(date) => setAssignment((prev) => ({
                                                ...prev,
                                                finishDate: date
                                            }))}
                                            showTimeSelect
                                            timeFormat="HH:mm"

                                            dateFormat="yyyy-MM-dd HH:mm"
                                        />
                                        {/*<DatePicker*/}
                                        {/*    selectsRange={true}*/}
                                        {/*    startDate={assignment.startDate}*/}
                                        {/*    endDate={assignment.finishDate}*/}
                                        {/*    onChange={(update) => {*/}
                                        {/*        setAssignment(prev => ({*/}
                                        {/*            ...prev,*/}
                                        {/*            startDate: update[0],*/}
                                        {/*            finishDate: update[1]*/}
                                        {/*        }))*/}
                                        {/*    }}*/}
                                        {/*    withPortal*/}
                                        {/*/>*/}

                                    </VStack>
                                    <VStack>
                                        <Wrap w={"100%"}>
                                            {assignment.emails && assignment.emails.map((email) => (
                                                <WrapItem key={email}>
                                                    <Tag
                                                        size={"sm"}
                                                        borderRadius="full"
                                                        variant="solid"
                                                        colorScheme="green"
                                                    >
                                                        <TagLabel>{email}</TagLabel>
                                                        <TagCloseButton
                                                            onClick={() => {
                                                                console.log(email)
                                                                setAssignment(prev => ({
                                                                    ...prev,
                                                                    emails: prev.emails.filter(e => e !== email)
                                                                }))
                                                            }}
                                                        />
                                                    </Tag>
                                                </WrapItem>
                                            ))}
                                        </Wrap>
                                        {/*INPUT & ADD BUTTOn*/}
                                        <HStack w={"100%"}>
                                            <Input
                                                size={"sm"}
                                                value={email}
                                                onChange={(e) => setEmail(e.target.value)}
                                                placeholder={
                                                    "Email to share. Ex: test@gmail.com"
                                                }
                                            />
                                            <Button
                                                onClick={() => {
                                                    if (!assignment.emails.includes(email)) {
                                                        setAssignment(prev => ({
                                                            ...prev,
                                                            emails: [...prev.emails, email]
                                                        }))
                                                    }
                                                    setEmail("");

                                                }}
                                                isDisabled={email.length === 0}
                                                size={"sm"}
                                                colorScheme={"purple"}
                                            >
                                                Add
                                            </Button>
                                        </HStack>
                                    </VStack>
                                    <Button
                                        size={'sm'}
                                        onClick={assignToUsers}
                                        alignSelf={'end'} colorScheme={'blue'}>Giao BÃ i</Button>
                                </VStack>
                            </Box>
                        </Flex>
                    </VStack>
                )
            }
        </Box>
    );
};

export default Assignment;
